import React from "react";
import {
  Box,
  Card,
  CardMedia,
  Typography,
  Slider,
  IconButton,
  Stack,
} from "@mui/material";
import MusicOffIcon from "@mui/icons-material/MusicOff";
import {
  FastForwardRounded,
  FastRewindRounded,
  PauseRounded,
  PlayArrowRounded,
  SkipPrevious,
  SkipNext,
  VolumeDownRounded,
  VolumeUpRounded,
} from "@mui/icons-material";

type AudioPlayerProps = {
  selectedLink: string | undefined;
};

function AudioPlayer({ selectedLink }: AudioPlayerProps) {
  const audioPlayerId = "audio-player";
  const [paused, setPaused] = React.useState(!!!selectedLink);
  const [position, setPosition] = React.useState<number | undefined>(undefined);
  const [endTime, setEndTime] = React.useState<number | undefined>(undefined);

  function formatDuration(time: number | undefined): string {
    if (!time) return "--:--";

    const dateObj = new Date(time * 1000);
    const hours = dateObj.getHours();
    const timeString = dateObj.toUTCString().split(' ')[4];

    return hours ? timeString : timeString.split(':')[1]
  }

  return (
    <>
      <Card sx={{ height: "50%" }}>
        <CardMedia
          sx={{
            width: "100%",
            height: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          {selectedLink ? (
            <audio
              controls
              autoPlay
              id={audioPlayerId}
              src={selectedLink}
              onPlay={() => setPaused(false)}
              onPause={() => setPaused(true)}
              onDurationChange={(event) => {
                setPosition(0);
                setEndTime(event.currentTarget.duration);
              }}
            ></audio>
          ) : (
            <div
              style={{
                height: "100%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                flexDirection: "column",
                fontSize: "75px",
              }}
            >
              <MusicOffIcon fontSize="inherit" />
              <Typography variant="h4">Add a link to play.</Typography>
            </div>
          )}
        </CardMedia>
      </Card>
      <Box
        sx={{
          height: "50%",
          display: "flex",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
        <Slider
          size="small"
          valueLabelDisplay="off"
          sx={{ width: "60%", mt: 5 }}
        />
        <Box
          sx={{
            width: "61%",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            mt: -1,
          }}
        >
          <Typography fontSize="0.75rem">{formatDuration(position)}</Typography>
          <Typography fontSize="0.75rem">{formatDuration(endTime)}</Typography>
        </Box>
        <Box
          sx={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            mt: 3,
          }}
        >
          <IconButton>
            <SkipPrevious sx={{ fontSize: "60px" }} />
          </IconButton>
          <IconButton>
            <FastRewindRounded sx={{ fontSize: "60px" }} />
          </IconButton>
          <IconButton
            onClick={() => {
              if (!selectedLink) return;

              const audioPlayer: HTMLAudioElement = document.getElementById(
                audioPlayerId,
              ) as HTMLAudioElement;

              if (paused) {
                audioPlayer.play();
              } else {
                audioPlayer.pause();
              }
            }}
          >
            {paused ? (
              <PlayArrowRounded sx={{ fontSize: "75px" }} />
            ) : (
              <PauseRounded sx={{ fontSize: "75px" }} />
            )}
          </IconButton>
          <IconButton>
            <FastForwardRounded sx={{ fontSize: "60px" }} />
          </IconButton>
          <IconButton>
            <SkipNext sx={{ fontSize: "60px" }} />
          </IconButton>
        </Box>
        <Stack
          spacing={2}
          direction="row"
          sx={{ mt: 3, px: 1, width: "50%" }}
          alignItems="center"
        >
          <VolumeDownRounded />
          <Slider defaultValue={30} />
          <VolumeUpRounded />
        </Stack>
      </Box>
    </>
  );
}

export default AudioPlayer;
