import React from "react";
import { Box, ThemeProvider, createTheme, Grid, Paper } from "@mui/material";

import { Header, Playlist, AudioPlayer, AddButton } from "./components";
import CssBaseline from "@mui/material/CssBaseline";

const darkTheme = createTheme({
  palette: {
    mode: "dark",
  },
});

export type KeyedLink = {link: string, id: string};

function App() {
  const [link, setLink] = React.useState(new Array<KeyedLink>());

  function handleData(linkFromChild: string) {
    setLinks([...links, {link: linkFromChild, id: `${linkFromChild}${Date.now()}`}]);
  }

  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Box>
        <Header></Header>
        <Grid container>
          <Grid xs={6}>
            <Paper
              elevation={5}
              sx={{
                m: 4,
                height: "85vh",
                maxHeight: "85vh",
                overflow: "auto",
                overflowX: "hidden",
              }}
            >
              <Playlist keyedLinks={links}></Playlist>
            </Paper>
          </Grid>
          <Grid xs={6}>
            <Paper
              elevation={5}
              sx={{
                m: 4,
                pt: 2,
                height: "85vh",
                maxHeight: "85vh",
                overflow: "auto",
                overflowX: "hidden",
              }}
            >
              <AudioPlayer></AudioPlayer>
            </Paper>
          </Grid>
        </Grid>
        <AddButton dataHandler={handleData}></AddButton>
      </Box>
    </ThemeProvider>
  );
}

export default App;
