import React from "react";
import { IconButton, List, ListItem, ListItemIcon, ListItemText, Paper, Stack, Typography } from "@mui/material";
import DehazeIcon from '@mui/icons-material/Dehaze';
import ClearIcon from '@mui/icons-material/Clear';
import { KeyedLink } from "../App";

type PlaylistProps = {
  keyedLinks: Array<KeyedLink>;
};

function Playlist({ keyedLinks }: PlaylistProps) {
  const [keyedLinksState, setKeyedLinksState] = React.useState(keyedLinks);

  const handleClick = (idOfLink: string) => {
    setKeyedLinksState(keyedLinksState.slice(
      keyedLinksState.findIndex(keyedLink => keyedLink.id === idOfLink), 1)
    );
  }

  return (
    <List sx={{ py: 0, alignItems: "center" }}>
      {keyedLinksState.map((keyedLink) => (
        <Paper sx={{ px: 2, mt: 1 }} key={keyedLink.id}>
          <ListItem secondaryAction={
            <IconButton edge="end" aria-label="delete" onClick={handleClick(keyedLink.id)}>
              <ClearIcon />
            </IconButton>
          }>
            <ListItemIcon>
              <DehazeIcon fontSize="large"/>
            </ListItemIcon>
            <ListItemText primary={keyedLink.link} primaryTypographyProps={{ fontSize: '25px' }} ></ListItemText>
          </ListItem>
        </Paper>
      ))}
    </List>
  );
}

export default Playlist;
