import React from "react";
import { Box, AppBar, ThemeProvider, Typography, createTheme, Fab, Grid } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";

// import { pageTheme } from "./assets/PageTheme";

import CssBaseline from '@mui/material/CssBaseline';

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
  },
});

function App() {
  const [anchor, setAnchor] = React.useState<null | HTMLElement>(null);

  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchor(anchor ? null : event.currentTarget);
  };

  const open = Boolean(anchor);
  const id = open ? 'simple-popper' : undefined;

  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Box>
        <AppBar sx={{ p: 2 }} >
          <Grid container sx={{ display: "flex", alignItems: 'center', }}>
            <Grid xs={11}>
              <Typography variant="h4" component="h1" sx={{ p: 2 }}>
                Audio Player for Streamed Content
              </Typography>
            </Grid>
            <Grid xs>
              <Fab color="default" aria-label="add">
                <AddIcon />
              </Fab> 
              <BasePopup id={id} open={open} anchor={anchor}>
        <PopupBody>The content of the Popup.</PopupBody>
      </BasePopup>

            </Grid> 
          </Grid>        
        </AppBar>
      </Box>
    </ThemeProvider>
  );
}

export default App;
