import React from "react";
import { Box, ThemeProvider, createTheme, Grid, Paper } from "@mui/material";

import { Header, Playlist, AudioPlayer, AddButton, KeyedLink } from "./components";
import CssBaseline from "@mui/material/CssBaseline";

const darkTheme = createTheme({
  palette: {
    mode: "dark",
  },
});

function App() {
  const [link, setLink] = React.useState<string | undefined>();

  function handleData(linkFromChild: string) {
    setLink(undefined);
    setLink(linkFromChild);
  }

  function keyLink(link: string | undefined): KeyedLink | undefined {
    if (link) return { link: link, id: `${link}${Date.now()}` };
  }

  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Box>
        <Header></Header>
        <Grid container>
          <Grid xs={6}>
            <Paper
              elevation={5}
              sx={{
                m: 4,
                height: "85vh",
                maxHeight: "85vh",
                overflow: "auto",
                overflowX: "hidden",
              }}
            >
              <Playlist link={keyLink(link)}></Playlist>
            </Paper>
          </Grid>
          <Grid xs={6}>
            <Paper
              elevation={5}
              sx={{
                m: 4,
                pt: 2,
                height: "85vh",
                maxHeight: "85vh",
                overflow: "auto",
                overflowX: "hidden",
              }}
            >
              <AudioPlayer></AudioPlayer>
            </Paper>
          </Grid>
        </Grid>
        <AddButton dataHandler={handleData}></AddButton>
      </Box>
    </ThemeProvider>
  );
}

export default App;
