import React from "react";
import { Fab, Fade, Popper, TextField } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";

function AddButton() {
    const [anchor, setAnchor] = React.useState<null | HTMLElement>(null);

    const handleClick = (event: React.MouseEvent<HTMLElement>) => {
      setAnchor(anchor ? null : event.currentTarget);
    };

    const open = Boolean(anchor);
    const id = open ? 'simple-popper' : undefined;

    return (
        <div>
            <Fab
                sx={{
                    position: "fixed",
                    bottom: (theme) => theme.spacing(2),
                    right: (theme) => theme.spacing(2)
                }}
                color="default"
                onClick={handleClick}
            >
                <AddIcon />
            </Fab> 
            <Popper id={id} open={open} anchorEl={anchor} placement="left-end" disablePortal={true} transition>
                {({ TransitionProps }) => (
                <Fade {...TransitionProps}>
                    <TextField sx={{width:"42.25vw"}} id="filled-basic" label="Add link to playlist" variant="filled" />
                </Fade>
                )}
            </Popper>
        </div>
    );
}

export default AddButton;
