import React from "react";
import { Card, CardActions, CardMedia, CircularProgress } from "@mui/material";
import PendingIcon from '@mui/icons-material/Pending';

type AudioPlayerProps = {
  selectedLink: string | undefined;
};

function AudioPlayer({ selectedLink }: AudioPlayerProps) {
  return (
    <Card sx={{ height: "50%" }}>
      <CardMedia
        sx={{ width: "100%", height: "50%", justifyContent: "center"}}
        src={selectedLink}
      >
        {(selectedLink) ? <audio src={selectedLink}></audio> : <PendingIcon />}
      </CardMedia>
    </Card>
  );
}

export default AudioPlayer;
